CREATE OR REPLACE FUNCTION obter_regra_mat_dose_frac ( cd_material_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


cd_classe_material_w           	integer;
cd_grupo_material_w            	smallint;
cd_subgrupo_material_w         	smallint;

ie_retorno_w			varchar(1)	:= 'N';
qt_regra_w			bigint;


BEGIN

select	max(cd_classe_material),
	max(cd_grupo_material),
	max(cd_subgrupo_material)
into STRICT	cd_classe_material_w,
	cd_grupo_material_w,
	cd_subgrupo_material_w
from 	estrutura_material_v
where	cd_material	= cd_material_p;

select	count(*)
into STRICT	qt_regra_w
from	regra_mat_dose_frac
where	coalesce(cd_classe_material, cd_classe_material_w) 		= cd_classe_material_w
and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_material, cd_material_p)				= cd_material_p
and	cd_perfil						= cd_perfil_p;

if (qt_regra_w > 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
