CREATE OR REPLACE FUNCTION obter_regra_medico_prescr_mat ( nr_interno_conta_p bigint, cd_convenio_p bigint, cd_material_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
qt_existe_w		bigint;
ie_retorno_w		varchar(1) := 'N';
cd_material_w		bigint;
cd_setor_atendimento_w	integer;
ie_receita_w		varchar(1);
qt_regra_w		bigint;

C01 CURSOR FOR
	SELECT	cd_material,
		cd_setor_atendimento
	from	material_atend_paciente
	where	nr_interno_conta = nr_interno_conta_p
	and	coalesce(cd_medico_prescritor::text, '') = ''
	and	coalesce(dt_acerto_convenio::text, '') = ''
	and	coalesce(cd_motivo_exc_conta::text, '') = '';


BEGIN

select	count(nr_sequencia)
into STRICT	qt_regra_w
from	regra_medico_prescr_mat;

if (coalesce(cd_material_p,0) > 0) and (qt_regra_w > 0) then

	select	coalesce(max(cd_grupo_material),0),
		coalesce(max(cd_subgrupo_material),0),
		coalesce(max(cd_classe_material),0)
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;

	select 	coalesce(max(ie_receita),'N')
	into STRICT	ie_receita_w
	from 	material
	where 	cd_material = cd_material_p;

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	regra_medico_prescr_mat
	where	((coalesce(cd_convenio, coalesce(cd_convenio_p,0)) = coalesce(cd_convenio_p,0)) or (coalesce(cd_convenio_p,0) = 0))
	and	((coalesce(cd_grupo_material, coalesce(cd_grupo_material_w,0)) = coalesce(cd_grupo_material_w,0)) or (coalesce(cd_grupo_material_w,0) = 0))
	and	((coalesce(cd_subgrupo_material, coalesce(cd_subgrupo_material_w,0)) = coalesce(cd_subgrupo_material_w, 0)) or (coalesce(cd_subgrupo_material_w, 0) = 0))
	and	((coalesce(cd_classe_material, coalesce(cd_classe_material_w, 0)) = coalesce(cd_classe_material_w,0)) or (coalesce(cd_classe_material_w,0) = 0))
	and	((coalesce(cd_material, coalesce(cd_material_p, 0)) = coalesce(cd_material_p, 0)) or (coalesce(cd_material_p, 0) = 0))
	and 	((coalesce(ie_receita,'A') = 'A') or (coalesce(ie_receita,'A') = ie_receita_w))
	and	((coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p, 0)) = coalesce(cd_setor_atendimento_p, 0)) or (coalesce(cd_setor_atendimento_p, 0) = 0))
	and	ie_situacao = 'A';

end if;

if (coalesce(nr_interno_conta_p, 0) > 0) and (qt_regra_w > 0) then

	open C01;
	loop
	fetch C01 into
		cd_material_w,
		cd_setor_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	coalesce(max(cd_grupo_material),0),
			coalesce(max(cd_subgrupo_material),0),
			coalesce(max(cd_classe_material),0)
		into STRICT	cd_grupo_material_w,
			cd_subgrupo_material_w,
			cd_classe_material_w
		from	estrutura_material_v
		where	cd_material = cd_material_w;

		select 	coalesce(max(ie_receita),'N')
		into STRICT	ie_receita_w
		from 	material
		where 	cd_material = cd_material_w;

		select	coalesce(max('S'),'N')
		into STRICT	ie_retorno_w
		from	regra_medico_prescr_mat
		where	((coalesce(cd_convenio, coalesce(cd_convenio_p,0)) = coalesce(cd_convenio_p,0)) or (coalesce(cd_convenio_p,0) = 0))
		and	((coalesce(cd_grupo_material, coalesce(cd_grupo_material_w,0)) = coalesce(cd_grupo_material_w,0)) or (coalesce(cd_grupo_material_w,0) = 0))
		and	((coalesce(cd_subgrupo_material, coalesce(cd_subgrupo_material_w,0)) = coalesce(cd_subgrupo_material_w, 0)) or (coalesce(cd_subgrupo_material_w, 0) = 0))
		and	((coalesce(cd_classe_material, coalesce(cd_classe_material_w, 0)) = coalesce(cd_classe_material_w,0)) or (coalesce(cd_classe_material_w,0) = 0))
		and (coalesce(cd_material, coalesce(cd_material_w, 0)) = coalesce(cd_material_w, 0))
		and 	((coalesce(ie_receita,'A') = 'A') or (coalesce(ie_receita,'A') = ie_receita_w))
		and	((coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_w, 0)) = coalesce(cd_setor_atendimento_w, 0)) or (coalesce(cd_setor_atendimento_w, 0) = 0))
		and	ie_situacao = 'A';
		end;

		if (ie_retorno_w = 'S') then
			goto final;
		end if;
	end loop;
	close C01;

end if;

<<final>>

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
