CREATE OR REPLACE FUNCTION obter_regra_med_patologia (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_setor_atendimento_w	integer;
ie_tipo_regra_w		varchar(15);


BEGIN

ie_tipo_regra_w := 'N';

select	cd_setor_atendimento
into STRICT	cd_setor_atendimento_w
from	prescr_medica pm
where	pm.nr_prescricao = nr_prescricao_p;

select	ie_tipo_regra
into STRICT	ie_tipo_regra_w
from	REGRA_LAUDO_PAT_MEDICO
where	cd_setor_atendimento = cd_setor_atendimento_w;

if (coalesce(ie_tipo_regra_w::text, '') = '') then
	 ie_tipo_regra_w := 'N';
end if;

return	ie_tipo_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
