CREATE OR REPLACE FUNCTION obter_regra_multiplo_req_auto ( cd_estabelecimento_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE



qt_existe_w			integer;
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;

qt_conversao_w			double precision;


BEGIN

qt_conversao_w		:= 1;

select	count(*)
into STRICT	qt_existe_w
from	regra_req_autom_qtde;

if (qt_existe_w > 0) then
	begin
	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;

	begin
	select	qt_multiplo
	into STRICT	qt_conversao_w
	from	regra_req_autom_qtde
	where	coalesce(cd_grupo_material, cd_grupo_material_w)			= cd_grupo_material_w
	and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)		= cd_subgrupo_material_w
	and	coalesce(cd_classe_material, cd_classe_material_w)			= cd_classe_material_w
	and	coalesce(cd_material, cd_material_p) 				= cd_material_p
	and	coalesce(cd_estabelecimento, cd_estabelecimento_p)		= cd_estabelecimento_p
	and	coalesce(ie_dia_semana, obter_cod_dia_semana(clock_timestamp()))		= obter_cod_dia_semana(clock_timestamp());
	exception when others then
		qt_conversao_w	:= 1;
	end;
	end;
end if;

return	qt_conversao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
