CREATE OR REPLACE FUNCTION obter_regra_nf_pat_equip ( cd_material_p bigint, cd_local_estoque_p bigint, cd_conta_contabil_p text, cd_estabelecimento_p bigint, ie_opcao_p text, ie_retorno_p text, vl_item_regra_p bigint default null, dt_vigencia_p timestamp default null) RETURNS varchar AS $body$
DECLARE



/*ie_retorno_p
E - Retorna se existe regra
D - Retorna se deseja desdobrar por quantidade
*/
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
ie_desdobrar_qtde_w		varchar(1)	:= 'N';
ds_retorno_w			varchar(1) 	:= 'N';
qt_registro_w			bigint	:= 0;

c01 CURSOR FOR
SELECT	coalesce(ie_desdobrar_qtde,'N')
from	regra_nf_pat_equip
where	coalesce(cd_grupo_material,cd_grupo_material_w) = cd_grupo_material_w
and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w) = cd_subgrupo_material_w
and	coalesce(cd_classe_material,cd_classe_material_w) = cd_classe_material_w
and	coalesce(cd_local_estoque,cd_local_estoque_p) = cd_local_estoque_p
and	coalesce(cd_conta_contabil,cd_conta_contabil_p) = cd_conta_contabil_p
and	coalesce(cd_material,cd_material_p) = cd_material_p
and	cd_estabelecimento = cd_estabelecimento_p
and	((ie_opcao_p = 'E' AND ie_equipamento = 'S') or
	 (ie_opcao_p = 'P' AND ie_patrimonio = 'S'))
and (coalesce(nr_seq_regra_valor::text, '') = '' or pat_obter_se_regra_valor(nr_seq_regra_valor,vl_item_regra_p, dt_vigencia_p) = 'S')
order by
	coalesce(cd_local_estoque,99999) asc,
	coalesce(cd_conta_contabil,'xxxxx') asc,
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0),
	coalesce(nr_seq_regra_valor,0);


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;

	if (ie_retorno_p = 'E') then

		select	count(*)
		into STRICT	qt_registro_w
		from	regra_nf_pat_equip
		where	coalesce(cd_grupo_material,cd_grupo_material_w) = cd_grupo_material_w
		and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w) = cd_subgrupo_material_w
		and	coalesce(cd_classe_material,cd_classe_material_w) = cd_classe_material_w
		and	coalesce(cd_local_estoque,cd_local_estoque_p) = cd_local_estoque_p
		and	coalesce(cd_conta_contabil,cd_conta_contabil_p) = cd_conta_contabil_p
		and	coalesce(cd_material,cd_material_p) = cd_material_p
		and	cd_estabelecimento = cd_estabelecimento_p
		and	((ie_opcao_p = 'E' AND ie_equipamento = 'S') or
			(ie_opcao_p = 'P' AND ie_patrimonio = 'S'))
		and (coalesce(nr_seq_regra_valor::text, '') = '' or pat_obter_se_regra_valor(nr_seq_regra_valor,vl_item_regra_p, dt_vigencia_p) = 'S');

		if (qt_registro_w > 0) then
			ds_retorno_w := 'S';
		end if;

	elsif (ie_retorno_p = 'D') then

		open C01;
		loop
		fetch C01 into
			ie_desdobrar_qtde_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			ds_retorno_w := ie_desdobrar_qtde_w;
			end;
		end loop;
		close C01;

	end if;

end if;
return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
