CREATE OR REPLACE FUNCTION obter_regra_obrig_proc (nr_seq_regra_p bigint, ie_tipo_atendimento_p bigint, cd_material_exame_p text) RETURNS varchar AS $body$
DECLARE


ie_regra_w	varchar(255);
ie_excluir_w	varchar(1);
cont_w		bigint;

C01 CURSOR FOR
SELECT	ie_excluir
from	regra_indic_clinica_atend
where	nr_seq_regra		= nr_seq_regra_p
and	coalesce(ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
and	coalesce(cd_material_exame,coalesce(cd_material_exame_p,'X'))	= coalesce(cd_material_exame_p,'X')
order by coalesce(ie_tipo_atendimento,99),
	 coalesce(cd_material_exame,'AAAAAAAAA');


BEGIN

ie_regra_w := 'S';

select	count(*)
into STRICT	cont_w
from	regra_indic_clinica_atend
where	nr_seq_regra		= nr_seq_regra_p
and	coalesce(ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
and	coalesce(cd_material_exame,coalesce(cd_material_exame_p,'X'))	= coalesce(cd_material_exame_p,'X');

if (cont_w > 0) then

	open C01;
	loop
	fetch C01 into
		ie_excluir_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;

end if;

if (ie_excluir_w = 'E') then
	ie_regra_w := 'N';
elsif (ie_excluir_w = 'I') then
	ie_regra_w := 'S';
end if;

return	ie_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
