CREATE OR REPLACE FUNCTION obter_regra_ocorrencia (nr_sequencia_p bigint, nr_seq_turno_p bigint, nr_seq_tipo_ocorrencia_p text) RETURNS varchar AS $body$
DECLARE


qt_reg_w	bigint;
ie_retorno_w	varchar(1);


BEGIN
select	count(*)
into STRICT	qt_reg_w
from	regra_ocorrencia_turno_lib
where	nr_seq_regra = nr_sequencia_p;

if (qt_reg_w > 0) then
	select	count(*)
	into STRICT	qt_reg_w
	from	regra_ocorrencia_turno a,
		regra_ocorrencia_turno_lib b
	where	a.nr_sequencia = b.nr_seq_regra
	and	coalesce(b.nr_seq_turno,coalesce(nr_seq_turno_p,0)) = coalesce(nr_seq_turno_p,0)
	and	b.nr_seq_tipo_ocorrencia = nr_seq_tipo_ocorrencia_p
	and	a.nr_sequencia = nr_sequencia_p;

	if (qt_reg_w > 0) then
		ie_retorno_w := 'S';
	else
		ie_retorno_w := 'N';
	end if;
else
	ie_retorno_w := 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
