SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_regra_orc_desconto ( vl_orcamento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


vl_retorno_w	double precision;
qt_reg_w	bigint;
pr_desconto_w	double precision;
vl_minimo_w	double precision;
vl_maximo_w	double precision;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	regra_orc_desconto;

if (coalesce(qt_reg_w,0) > 0) then

	select	max(pr_desconto),
		max(vl_minimo),
		max(vl_maximo)
	into STRICT	pr_desconto_w,
		vl_minimo_w,
		vl_maximo_w
	from	regra_orc_desconto
	where	vl_orcamento_p between vl_minimo and vl_maximo;

	if (ie_opcao_p	= 'DES') then
		vl_retorno_w	:= pr_desconto_w;
	elsif (ie_opcao_p	= 'MIN') then
		vl_retorno_w	:= vl_minimo_w;
	elsif (ie_opcao_p	= 'MAX') then
		vl_retorno_w	:= vl_maximo_w;
	end if;

end if;

vl_retorno_w	:= coalesce(vl_retorno_w,-1);

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
