CREATE OR REPLACE FUNCTION obter_regra_ordem_compra (ie_tipo_ordem_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
C	Comprador
F	Frete
L	Local
*/
cd_comprador_w		varchar(10);
ie_frete_w			varchar(15);
cd_local_estoque_w	smallint;

ds_retorno_w		varchar(15);


BEGIN

	select	cd_comprador,
		ie_frete,
		cd_local_estoque
	into STRICT	cd_comprador_w,
		ie_frete_w,
		cd_local_estoque_w
	from	regra_padronizacao_oc
	where	ie_tipo_ordem = ie_tipo_ordem_p;

if (ie_opcao_p = 'C') and (cd_comprador_w IS NOT NULL AND cd_comprador_w::text <> '') then
	ds_retorno_w := cd_comprador_w;
elsif (ie_opcao_p = 'F') and (ie_frete_w IS NOT NULL AND ie_frete_w::text <> '') then
	ds_retorno_w := ie_frete_w;
elsif (ie_opcao_p = 'L') and (cd_local_estoque_w IS NOT NULL AND cd_local_estoque_w::text <> '') then
	ds_retorno_w := cd_local_estoque_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
