CREATE OR REPLACE FUNCTION obter_regra_orient_alta ( ie_tipo_evolucao_p text, cd_pessoa_fisica_p text, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_w	bigint;
ie_visualiza_w	varchar(1) := 'S';


BEGIN
select	count(*)
into STRICT	qt_regra_w
from	regra_orient_alta
where	ie_tipo_evolucao = ie_tipo_evolucao_p;

if (qt_regra_w > 0) then
	begin
	select	count(*)
	into STRICT	qt_regra_w
	from	regra_orient_alta
	where	ie_tipo_evolucao = ie_tipo_evolucao_p
	and	coalesce(cd_perfil,cd_perfil_p)	= cd_perfil_p
	and	coalesce(cd_pessoa_fisica,cd_pessoa_fisica_p)	= cd_pessoa_fisica_p;

	if (qt_regra_w	= 0) then
		ie_visualiza_w	:= 'N';
	end if;
	end;
end if;

return	ie_visualiza_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
