CREATE OR REPLACE FUNCTION obter_regra_pacote_alta (cd_estabelecimento_p bigint, ie_tipo_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_regra_pacote_alta_w		varchar(1);

C01 CURSOR FOR
	SELECT	'S'
	from	regra_geracao_pacote_alta
	where	cd_estabelecimento 	= cd_estabelecimento_p
	and	ie_tipo_atendimento	= ie_tipo_atendimento_p
	and	ie_situacao		= 'A'
	order by ie_tipo_atendimento;


BEGIN

ie_regra_pacote_alta_w	:= 'N';

open C01;
loop
fetch C01 into
	ie_regra_pacote_alta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_regra_pacote_alta_w	:= ie_regra_pacote_alta_w;
	end;
end loop;
close C01;


return	ie_regra_pacote_alta_w;

end;
$body$
