CREATE OR REPLACE FUNCTION obter_regra_pano_icone_click (cd_icone_p bigint ) RETURNS bigint AS $body$
DECLARE


nr_seq_regra_w		bigint;
ie_possui_regra_w	bigint := 0;


BEGIN

select	max(a.nr_sequencia)
into STRICT 	nr_seq_regra_w
from	regra_perm_panorama a
where	a.cd_perfil = obter_perfil_ativo
and exists (	SELECT	1
		from	regra_panorama_icone_click x
		where	x.nr_seq_regra = a.nr_sequencia);

if (coalesce(nr_seq_regra_w::text, '') = '') then -- se n√£o encontrou a regra para o perfil, busca uma regra sem perfil definido.
	select	max(a.nr_sequencia)
	into STRICT 	nr_seq_regra_w
	from	regra_perm_panorama a
	where	coalesce(a.cd_perfil::text, '') = ''
	and exists (	SELECT	1
			from	regra_panorama_icone_click x
			where	x.nr_seq_regra = a.nr_sequencia);
end if;

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	select	coalesce(count(*), 0)
	into STRICT	ie_possui_regra_w
	from	regra_panorama_icone_click
	where	nr_seq_regra = nr_seq_regra_w
	and	cd_icone_panorama = cd_icone_p;
end if;

return ie_possui_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_regra_pano_icone_click (cd_icone_p bigint ) FROM PUBLIC;

