CREATE OR REPLACE FUNCTION obter_regra_perfil_setor_ocup ( cd_perfil_p bigint, cd_setor_Atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_w		integer;
qt_regra_setor_w	integer;
ds_retorno_w		varchar(1)	:= 'S';


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	regra_exib_setor_ocup
where	cd_perfil	= cd_perfil_p;

if (qt_regra_w	> 0) then
	select	count(*)
	into STRICT	qt_regra_setor_w
	from	regra_exib_setor_ocup
	where	cd_perfil		= cd_perfil_p
	and	cd_Setor_Atendimento	= cd_setor_Atendimento_p;
	if (qt_regra_setor_w	 = 0) then
		ds_Retorno_w	:= 'N';
	end if;
else
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
