SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_regra_popup ( nr_seq_objeto_p bigint, ie_tipo_regra_p text default 'E', ie_opcao_p text DEFAULT NULL) RETURNS varchar AS $body$
DECLARE


ie_enable_w		varchar(1);
ie_visible_w	varchar(1);
qt_registro_w	integer;

C01 CURSOR FOR
	SELECT	ie_enable,
			ie_visible
	from	funcao_popup_regra
    where	nr_seq_objeto = nr_seq_objeto_p
    and		ie_tipo_regra = ie_tipo_regra_p
    and		coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento
    and		coalesce(cd_perfil, wheb_usuario_pck.get_cd_perfil) = wheb_usuario_pck.get_cd_perfil
    and		coalesce(nm_usuario_regra, wheb_usuario_pck.get_nm_usuario) = wheb_usuario_pck.get_nm_usuario
    order by
		coalesce(nm_usuario_regra,'AAA'),
		coalesce(cd_perfil,0),
		coalesce(cd_estabelecimento,0);

BEGIN

select	count(1)
into STRICT	qt_registro_w
from	funcao_popup_regra
where	nr_seq_objeto = nr_seq_objeto_p
and		ie_tipo_regra = ie_tipo_regra_p  LIMIT 1;

if (qt_registro_w > 0) then
	open C01;
	loop
	fetch C01 into
		ie_enable_w,
		ie_visible_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
else

	Select	coalesce(ie_visible,'S')
	into STRICT   	ie_visible_w
	from   	dic_objeto
	where	nr_sequencia = nr_seq_objeto_p  LIMIT 1;

	ie_enable_w := ie_visible_w;
end if;

if (ie_opcao_p = 'E') then -- Enable
	return	ie_enable_w;
elsif (ie_opcao_p = 'V') then -- Visible
	return	ie_visible_w;
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
