CREATE OR REPLACE FUNCTION obter_regra_porte_cirurgia (nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_porte_w		varchar(1):=null;
qt_regra_w		bigint;
nr_min_duracao_real_w	bigint;


BEGIN

if	coalesce(nr_cirurgia_p ,0) > 0 then
	select	count(*)
	into STRICT	qt_regra_w
	from	regra_porte_cirurgia;

	if (qt_regra_w > 0) then
		select	coalesce(nr_min_duracao_real,0)
		into STRICT	nr_min_duracao_real_w
		from	cirurgia
		where	nr_cirurgia = nr_cirurgia_p;

		if (nr_min_duracao_real_w > 0) then
			select	max(ie_porte)
			into STRICT	ie_porte_w
			from	regra_porte_cirurgia
			where	nr_min_duracao_real_w between coalesce(qt_tempo_minimo,0) and coalesce(qt_tempo_maximo,99999);
		end if;
	end if;
end if;

return	ie_porte_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
