CREATE OR REPLACE FUNCTION obter_regra_preco_filme ( cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ie_regra_w			varchar(1);


BEGIN
select coalesce(max(ie_regra),'X')
into STRICT 	ie_regra_w
from	Convenio_regra_Preco_Filme
where cd_convenio		= cd_convenio_p;
if (ie_regra_w	= 'X') then
	select coalesce(max(ie_regra),'N')
	into STRICT 	ie_regra_w
	from	Convenio_regra_Preco_Filme
	where coalesce(cd_convenio::text, '') = '';
end if;

RETURN ie_regra_w;

END;
$body$
