CREATE OR REPLACE FUNCTION obter_regra_previsao_alta ( nr_prescricao_p bigint, nr_atendimento_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
nr_sequencia_w				bigint;
cd_estabelecimento_w		smallint;
cd_perfil_w					integer;
ie_limpa_campos_w			varchar(1);
ds_retorno_w				varchar(1) := 'N';


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then 
	 
	select	coalesce(max(ie_limpa_campos), 'X') 
	into STRICT	ie_limpa_campos_w 
	from 	regra_previsao_alta 
	where 	coalesce(cd_estabelecimento, cd_estabelecimento_p)	= cd_estabelecimento_p 
	and		coalesce(cd_perfil, cd_perfil_p)	= cd_perfil_p 
	and 	coalesce(nm_usuario_dest, nm_usuario_p) = nm_usuario_p 
	and (coalesce(ie_prescr_alta, 'N') = 'N' or (coalesce(ie_prescr_alta, 'N') = 'S' and coalesce(obter_se_prescr_alta(nr_prescricao_p),'N') = 'S')) 
	and (coalesce(ie_paciente_internado, 'N') = 'N' or (coalesce(ie_paciente_internado, 'N') = 'S' and coalesce(Obter_Se_Atend_Internado(nr_atendimento_p), 'N') = 'S'));
 
	if (ie_limpa_campos_w = 'S') then 
		ds_retorno_w := 'B';
	elsif (ie_limpa_campos_w = 'N') then 
		ds_retorno_w := 'S';	
	else 
		ds_retorno_w := 'N';
	end if;
 
end if;
 
return	ds_retorno_w;
 
end;
