CREATE OR REPLACE FUNCTION obter_regra_proc_sigla (cd_procedimento_p bigint, ie_origem_proc_p text, cd_tipo_procedimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(100) := '';


BEGIN
if (cd_tipo_procedimento_p = '') then
	select 	max(ds_sigla)
	into STRICT 	ds_retorno_w
	from 	regra_sigla_proc
	where 	CD_PROCEDIMENTO = cd_procedimento_p
	and	IE_ORIGEM_PROCED = ie_origem_proc_p;
else
	select 	max(substr(ds_sigla,1,100))
	into STRICT 	ds_retorno_w
	from 	regra_sigla_proc
	where 	cd_tipo_procedimento = cd_tipo_procedimento_p;

	if ((ds_retorno_w = '') or (coalesce(ds_retorno_w::text, '') = '')) then
		select 	max(ds_sigla)
		into STRICT 	ds_retorno_w
		from 	regra_sigla_proc
		where 	CD_PROCEDIMENTO = cd_procedimento_p
		and	IE_ORIGEM_PROCED = ie_origem_proc_p;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
