CREATE OR REPLACE FUNCTION obter_regra_prot_tipo_guia ( nr_interno_conta_p bigint, ie_tipo_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
ds_retorno_w			varchar(1)	:= 'S';
cd_convenio_w			integer;
qt_registros_w			integer	:= 0;
qt_reg_regra_w			integer	:= 0;
ie_tipo_guia_w			varchar(2);
cd_estabelecimento_w		smallint;

 
 

BEGIN 
 
 
select	cd_convenio_parametro, 
	ie_tipo_guia, 
	cd_estabelecimento 
into STRICT	cd_convenio_w, 
	ie_tipo_guia_w, 
	cd_estabelecimento_w 
from	conta_paciente		 
where	nr_interno_conta	= nr_interno_conta_p;
 
 
select	count(*) 
into STRICT	qt_registros_w 
from	regra_prot_tipo_guia 
where	cd_convenio		= cd_convenio_w 
and	ie_tipo_protocolo	= ie_tipo_protocolo_p 
and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_w,0)) = coalesce(cd_estabelecimento_w,0);
 
 
if	(qt_registros_w > 0  AND ie_tipo_guia_w IS NOT NULL AND ie_tipo_guia_w::text <> '')then 
	select	count(*) 
    into STRICT	qt_reg_regra_w 
    from	regra_prot_tipo_guia 
    where	cd_convenio						= cd_convenio_w 
    and	ie_tipo_protocolo 					= ie_tipo_protocolo_p 
	and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_w,0))	= coalesce(cd_estabelecimento_w,0) 
    and	ie_tipo_guia						= ie_tipo_guia_w;
 
     
    if (qt_reg_regra_w	= 0) then 
		ds_retorno_w	:= 'N';
    end if;
end if;
 
return	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
