CREATE OR REPLACE FUNCTION obter_regra_repasse_item (nr_seq_repasse_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  varchar(255);
nr_seq_regra_w bigint;


BEGIN

  ds_retorno_w  := '';

  SELECT coalesce(MAX(nr_seq_regra), 0)
  INTO STRICT   nr_seq_regra_w
  FROM   pls_repasse_mens_item
  WHERE  nr_seq_repasse = nr_seq_repasse_p;

  IF (nr_seq_regra_w > 0) then
    ds_retorno_w := to_char(nr_seq_regra_w);
  END IF;

RETURN  ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
