CREATE OR REPLACE FUNCTION obter_regra_req_permissao_aut ( cd_local_estoque_destino_p bigint, cd_local_estoque_p bigint, cd_perfil_p bigint, nm_usuario_p text, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


/*dados:
P	= Permissão
S	= Sequência
*/
ie_permissao_w		varchar(1) := 'S';
nr_sequencia_w		varchar(10);
qt_registro_w		bigint;
ds_retorno_w		varchar(10);


BEGIN

if (ie_retorno_p = 'P') then
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from	regra_requisicao_permissao
	where	coalesce(cd_local_estoque,cd_local_estoque_p)			= cd_local_estoque_p
	and	coalesce(cd_local_estoque_destino, cd_local_estoque_destino_p) 	= cd_local_estoque_destino_p
	and	coalesce(cd_perfil,cd_perfil_p)					= cd_perfil_p
	and	coalesce(nm_usuario_regra,nm_usuario_p) 				= nm_usuario_p
	and	ie_permissao_automatica = 'N';

	if (qt_registro_w > 0) then
		ie_permissao_w := 'N';
	end if;

	ds_retorno_w	:= ie_permissao_w;

	end;
elsif (ie_retorno_p = 'S') then
	begin
	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	regra_requisicao_permissao
	where	coalesce(cd_local_estoque,cd_local_estoque_p)			= cd_local_estoque_p
	and	coalesce(cd_local_estoque_destino, cd_local_estoque_destino_p) 	= cd_local_estoque_destino_p
	and	coalesce(cd_perfil,cd_perfil_p)					= cd_perfil_p
	and	coalesce(nm_usuario_regra,nm_usuario_p) 				= nm_usuario_p
	and	ie_permissao_automatica = 'N';

	ds_retorno_w	:= nr_sequencia_w;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
