CREATE OR REPLACE FUNCTION obter_regra_schem_order (nr_seq_obj_schematic_p obj_schem_order.nr_seq_obj_schematic%type, nr_seq_funcao_schematic_p obj_schem_order.nr_seq_funcao_schematic%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, cd_perfil_p perfil.cd_perfil%type) RETURNS varchar AS $body$
DECLARE


count_w	bigint;
nr_seq_regra_w	obj_schem_order.nr_sequencia%type;
ds_retorno_w	varchar(4000);
nr_seq_regra_perfil_w	obj_schem_order_perfil.nr_sequencia%type;
nr_seq_regra_estab_w	obj_schem_order_estab.nr_sequencia%type;

c01 CURSOR FOR
SELECT	nr_sequencia
from	obj_schem_order
where	nr_seq_obj_schematic = nr_seq_obj_schematic_p
or			nr_seq_funcao_schematic = nr_seq_funcao_schematic_p
order by nr_sequencia;

c01_w		c01%rowtype;


BEGIN

select	count(nr_sequencia)
into STRICT	count_w
from	obj_schem_order
where	nr_seq_obj_schematic = nr_seq_obj_schematic_p
or			nr_seq_funcao_schematic = nr_seq_funcao_schematic_p;

if (count_w > 0) then

	open c01;
	loop
	fetch c01 into	
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		if (coalesce(ds_retorno_w, 'X') <> 'X') then
			ds_retorno_w	:= ds_retorno_w || ',';
		end if;
		select	max(nr_sequencia)
		into STRICT		nr_seq_regra_perfil_w
		from	obj_schem_order_perfil
		where nr_seq_regra = c01_w.nr_sequencia
		and	cd_perfil = cd_perfil_p;
		if (coalesce(nr_seq_regra_perfil_w, 0) <> 0) then
			ds_retorno_w	:= ds_retorno_w || c01_w.nr_sequencia;
		else
			select	max(nr_sequencia)
			into STRICT		nr_seq_regra_estab_w
			from	obj_schem_order_estab
			where nr_seq_regra = c01_w.nr_sequencia
			and	cd_estabelecimento = cd_estabelecimento_p;
			if (coalesce(nr_seq_regra_estab_w, 0) <> 0) then
				ds_retorno_w	:= ds_retorno_w || c01_w.nr_sequencia;
			end if;
		end if;
	end;
	end loop;
	close c01;
end if;

return	ds_retorno_w;

end;
