CREATE OR REPLACE FUNCTION obter_regra_setor_lib_vis ( cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_regra_p text) RETURNS varchar AS $body$
DECLARE


c01 CURSOR FOR
	SELECT	b.cd_setor_atendimento
	from	regra_setor_lib_visita a,
			regra_setor_lib_vis_item b
	where	a.nr_sequencia = b.nr_seq_set_lib_vis
	and		coalesce(a.ie_situacao,'A') = 'A'
	and		coalesce(a.nm_usuario_regra, nm_usuario_regra_p) = nm_usuario_regra_p
	and		coalesce(a.cd_perfil, cd_perfil_p) = cd_perfil_p
	and		coalesce(a.cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p;

ds_setor_liberado_w		varchar(4000);
ds_setores_liberados_w	varchar(4000);


BEGIN
open c01;
loop
fetch c01 into
	ds_setor_liberado_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_setores_liberados_w	:= ds_setores_liberados_w || ds_setor_liberado_w || ',';
	end;
end loop;
close c01;

return	ds_setores_liberados_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
