CREATE OR REPLACE FUNCTION obter_regra_sms_medico ( nr_seq_regra_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE


ie_excecao_w   varchar(1);
ie_excecao_ww  varchar(1);

C01 CURSOR FOR
SELECT   coalesce(ie_excecao,'N')
from     regra_envio_sms_medico
where    nr_seq_regra               	= nr_seq_regra_p
and      coalesce(ie_situacao,'A')      	 	= 'A'
and      coalesce(cd_medico,cd_medico_p) 	= cd_medico_p
order by coalesce(cd_medico,0);



BEGIN
open C01;
loop
fetch C01 into
ie_excecao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_excecao_ww := ie_excecao_w;
	end;
end loop;
close C01;

return coalesce(ie_excecao_ww,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
