SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_regra_subst_com ( nr_prescricao_p bigint, cd_material_p bigint, nr_atendimento_cpoe_p bigint default null) RETURNS bigint AS $body$
DECLARE


cd_material_ret_w	material.cd_material%type;
qt_regra_w		bigint;
cd_material_generico_w	material.cd_material%type;
qt_material_generico_w	bigint;
nr_atendimento_w	atendimento_paciente.nr_atendimento%type;
cd_convenio_w		convenio.cd_convenio%type;



BEGIN

cd_material_ret_w	:= 0;

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

if (coalesce(coalesce(nr_atendimento_w, nr_atendimento_cpoe_p), 0) > 0) then

	cd_convenio_w := obter_convenio_atendimento(coalesce(nr_atendimento_w,nr_atendimento_cpoe_p));

	if (coalesce(cd_convenio_w, 0) > 0) then

		select	count(*)
		into STRICT	qt_regra_w
		from	rep_regra_subst_com
		where	cd_convenio		= cd_convenio_w
		and	coalesce(ie_situacao, 'A')	<> 'I';

		if (qt_regra_w > 0) then

			select	max(cd_material_generico)
			into STRICT	cd_material_generico_w
			from	material
			where	cd_material		= cd_material_p
			and	ie_tipo_material	= 2
			and	(cd_material_generico IS NOT NULL AND cd_material_generico::text <> '');

			if (coalesce(cd_material_generico_w, 0) > 0) then

				select	count(*)
				into STRICT	qt_material_generico_w
				from	material
				where	cd_material		= cd_material_generico_w
				and	ie_tipo_material	= 3
				and	ie_situacao	 	= 'A'
				and	ie_prescricao		= 'S';

				if (qt_material_generico_w > 0) then

					cd_material_ret_w	:= cd_material_generico_w;

				end if;

			end if;

		end if;

	end if;

end if;

return	cd_material_ret_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
