CREATE OR REPLACE FUNCTION obter_regra_taxa_preco_dupl ( nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	chr(13)|| cd_procedimento ||' - '|| obter_descricao_procedimento(cd_procedimento,ie_origem_proced)
	from	procedimento_paciente a
	where	a.nr_interno_conta		= nr_interno_conta_p
	and	coalesce(a.NR_SEQ_REGRA_PRECO,0)	<> 0
	and	coalesce(a.cd_motivo_exc_conta::text, '') = ''
	GROUP BY cd_procedimento,
		 ie_origem_proced;

ds_retorno_w		varchar(400);

BEGIN

open C01;
loop
fetch C01 into
	ds_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	ds_retorno_w;

END Obter_regra_Taxa_Preco_dupl HAVING count(*) >1
	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
