SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_regra_tempo_status (QT_MINUTO_P bigint, CD_SETOR_ATEND_P bigint, IE_STATUS_P text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(1);
qt_min_w			bigint;
qt_max_w			bigint;
nr_seq_w			bigint;

/*
M - menor que o tempo definido na regra
E - entre os tempos mínimo e máximo definidos na regra
A - Acima do tempo máximo definido na regra
*/
BEGIN

if (CD_SETOR_ATEND_P IS NOT NULL AND CD_SETOR_ATEND_P::text <> '') and (IE_STATUS_P IS NOT NULL AND IE_STATUS_P::text <> '') then


	select 	max(nr_sequencia)
	into STRICT 	nr_seq_w
	from 	setor_atend_tempo_status
	where 	cd_setor_atendimento = CD_SETOR_ATEND_P
	and 	ie_status_tempo = IE_STATUS_P;

	if (nr_seq_w > 0) then
		select 	max(qt_tempo_minimo),
				max(qt_tempo_maximo)
		into STRICT	qt_min_w,
				qt_max_w
		from	setor_atend_tempo_status
		where 	nr_sequencia = nr_seq_w;
	end if;

	if (qt_min_w IS NOT NULL AND qt_min_w::text <> '') and (qt_max_w IS NOT NULL AND qt_max_w::text <> '') then
		if (QT_MINUTO_P < qt_min_w) then
			ds_retorno_w := 'M';
		elsif (QT_MINUTO_P >= qt_min_w) and (QT_MINUTO_P <= qt_max_w)then
			ds_retorno_w := 'E';
		elsif (QT_MINUTO_P > qt_max_w) then
			ds_retorno_w := 'A';
		end if;
	end if;
end if;



RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
