CREATE OR REPLACE FUNCTION obter_regra_tipo_alerta_obs ( cd_perfil_p perfil.cd_perfil%type, nr_seq_regra_p adep_tipo_observacao.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	adep_tipo_observacao.ie_alerta%type;

cRegras CURSOR FOR
SELECT	a.ie_alerta,
		b.cd_perfil cd_perfil
from	adep_tipo_observacao a,
		regra_adep_tipo_obs b
where	a.nr_sequencia = b.nr_seq_regra
and		a.nr_sequencia = nr_seq_regra_p
and		b.cd_perfil = cd_perfil_p

union

select	a.ie_alerta,
		99999 cd_perfil
from	adep_tipo_observacao a
where	a.nr_sequencia = nr_seq_regra_p
and		not exists (	select	1
					from	regra_adep_tipo_obs b
					where 	a.nr_sequencia = b.nr_seq_regra)
order by cd_perfil;

BEGIN

for cRegras_w in cRegras loop
	ie_retorno_w := cRegras_w.ie_alerta;
	exit;
end loop;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
