CREATE OR REPLACE FUNCTION obter_regra_tipo_atend_porte (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ie_consiste_anest_port_w	varchar(1);
ie_tipo_atendimento_w		smallint;
cd_procedencia_w		integer;


BEGIN 
 
select	max(ie_tipo_atendimento), 
	max(cd_procedencia) 
into STRICT	ie_tipo_atendimento_w, 
	cd_procedencia_w 
from	atendimento_paciente 
where 	nr_atendimento	= nr_atendimento_p;
 
select	coalesce(max(ie_consiste_anest_port),'N') 
into STRICT	ie_consiste_anest_port_w 
from	regra_tipo_atend_porte 
where	coalesce(ie_tipo_atendimento, ie_tipo_atendimento_w) = ie_tipo_atendimento_w 
and	coalesce(cd_procedencia, cd_procedencia_w)		= cd_procedencia_w;
 
 
return	ie_consiste_anest_port_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
