CREATE OR REPLACE FUNCTION obter_regra_tipo_contrato ( cd_tipo_contrato_p bigint, nm_usuario_lib_p text) RETURNS varchar AS $body$
DECLARE


ie_resultado_w		varchar(1) := 'S';
cd_perfil_w		integer;

c01 CURSOR FOR
SELECT	ie_permite
from	tipo_contrato_regra_lib
where	nr_seq_tipo_contrato		= cd_tipo_contrato_p
and	coalesce(cd_perfil, cd_perfil_w)		= cd_perfil_w
and	coalesce(nm_usuario_lib, nm_usuario_lib_p)	= nm_usuario_lib_p
order by
	coalesce(cd_perfil,99999) desc,
	coalesce(nm_usuario_lib, 'XXXX') desc;


BEGIN

cd_perfil_w		:= coalesce(wheb_usuario_pck.get_cd_perfil,0);

open C01;
loop
fetch C01 into
	ie_resultado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_resultado_w := ie_resultado_w;
	end;
end loop;
close C01;


return	ie_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
