CREATE OR REPLACE FUNCTION obter_regra_tipo_tit ( cd_tributo_p bigint, cd_estabelecimento_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


/*
SI = Somar Imposto
BR = Base rendimento
*/
ds_retorno_w		bigint;

BEGIN

if (ie_tipo_p = 'SI') then
	select	count(*)
	into STRICT	ds_retorno_w
	from	dirf_regra_tributo r
	where	r.cd_tributo = cd_tributo_p
	and	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
	and	ie_somar_imposto = 'S';

elsif (ie_tipo_p = 'BR') then
	select	count(*)
	into STRICT	ds_retorno_w
	from	dirf_regra_tributo r
	where	r.cd_tributo					= cd_tributo_p
	and	coalesce(cd_estabelecimento,cd_estabelecimento_p)	= cd_estabelecimento_p
	and	ie_base_tit_rendimento				= 'S'  LIMIT 1;
elsif (ie_tipo_p = 'PM') then
	select	count(1)
	into STRICT	ds_retorno_w
	from	dirf_regra_tributo r
	where	r.cd_tributo					= cd_tributo_p
	and	coalesce(cd_estabelecimento,cd_estabelecimento_p)	= cd_estabelecimento_p
	and	ie_base_tit_rendimento				= 'P'  LIMIT 1;
elsif (ie_tipo_p = 'BN') then
	select	count(1)
	into STRICT	ds_retorno_w
	from	dirf_regra_tributo r
	where	r.cd_tributo					= cd_tributo_p
	and	coalesce(cd_estabelecimento,cd_estabelecimento_p)	= cd_estabelecimento_p
	and	ie_base_tit_rendimento				= 'F'  LIMIT 1;
elsif (ie_tipo_p = 'AD') then
	select	count(1)
	into STRICT	ds_retorno_w
	from	dirf_regra_tributo r
	where	r.cd_tributo					= cd_tributo_p
	and	coalesce(cd_estabelecimento,cd_estabelecimento_p)	= cd_estabelecimento_p
	and	ie_base_tit_rendimento				= 'A'  LIMIT 1;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
