CREATE OR REPLACE FUNCTION obter_regra_uso_qtde_proc (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
ie_opcao_p		1 - IE_ACAO_EXCESSO 
			2 - QT_EXCEDIDA 
			3 - DS_ERRO 
*/
 
 
ds_retorno_w		varchar(255);
nr_atendimento_w	bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
ie_acao_excesso_w	varchar(5);
ds_erro_w		varchar(255);
qt_excedida_w		double precision;

 

BEGIN 
 
select	nr_atendimento, 
	cd_procedimento, 
	ie_origem_proced 
into STRICT	nr_atendimento_w, 
	cd_procedimento_w, 
	ie_origem_proced_w 
from	procedimento_paciente 
where	nr_sequencia	= nr_sequencia_p;
 
SELECT * FROM obter_regra_qtde_proc(NR_ATENDIMENTO_w, CD_PROCEDIMENTO_w, IE_ORIGEM_PROCED_w, NR_SEQUENCIA_p, IE_ACAO_EXCESSO_w, DS_ERRO_w, qt_excedida_w) INTO STRICT IE_ACAO_EXCESSO_w, DS_ERRO_w, qt_excedida_w;
 
if (ie_opcao_p	= '1') then 
	ds_retorno_w	:= IE_ACAO_EXCESSO_w;
elsif (ie_opcao_p	= '2') then 
	ds_retorno_w	:= qt_excedida_w;
else 
	ds_retorno_w	:= ds_erro_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
