CREATE OR REPLACE FUNCTION obter_regra_valid_usuario_gpl ( ds_procedure_p text, cd_convenio_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
0 - ds_convenio_ws_w
1 - ds_url_ws_w
2 - ds_wsdl_pesquisa_w
3 - ds_url_ws_solic_w
4 - ds_wsdl_solic_w
5 - ds_url_ws_atual_w
6 - ds_wsdl_atual_w
*/
ds_retorno_w		varchar(255);
ds_convenio_ws_w	varchar(255);
ds_url_ws_w		varchar(255);
ds_wsdl_pesquisa_w      varchar(255);
ds_url_ws_solic_w       varchar(255);
ds_wsdl_solic_w         varchar(255);
ds_url_ws_atual_w       varchar(255);
ds_wsdl_atual_w         varchar(255);



BEGIN

select	max(ds_convenio_ws),
	max(ds_url_ws),
	max(ds_wsdl_pesquisa),
	max(ds_url_ws_solic),
	max(ds_wsdl_solic),
	max(ds_url_ws_atual),
	max(ds_wsdl_atual)
into STRICT	ds_convenio_ws_w,
	ds_url_ws_w,
	ds_wsdl_pesquisa_w,
	ds_url_ws_solic_w,
	ds_wsdl_solic_w,
	ds_url_ws_atual_w,
	ds_wsdl_atual_w
from	regra_validacao_usuario
where	cd_convenio		= cd_convenio_p
and	ds_procedure_validacao	= ds_procedure_p;

if (ie_opcao_p = 0) then
	ds_retorno_w	:= ds_convenio_ws_w;
elsif (ie_opcao_p = 1) then
	ds_retorno_w	:= ds_url_ws_w;
elsif (ie_opcao_p = 2) then
	ds_retorno_w	:= ds_wsdl_pesquisa_w;
elsif (ie_opcao_p = 3) then
	ds_retorno_w	:= ds_url_ws_solic_w;
elsif (ie_opcao_p = 4) then
	ds_retorno_w	:= ds_wsdl_solic_w;
elsif (ie_opcao_p = 5) then
	ds_retorno_w	:= ds_url_ws_atual_w;
elsif (ie_opcao_p = 6) then
	ds_retorno_w	:= ds_wsdl_atual_w;
end if;

return	ds_retorno_w;

end;
