CREATE OR REPLACE FUNCTION obter_regra_valor_ordem_consig ( cd_estabelecimento_p bigint, cd_cnpj_p text, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
ie_valor_consignado_w			varchar(1);
ie_valor_parametro_w			varchar(1);
ie_valor_regra_w			varchar(1);

c01 CURSOR FOR
SELECT	coalesce(ie_valor_consignado,'N')
from	regra_valor_ordem_consig
where	cd_estabelecimento = cd_estabelecimento_p
and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
and	((coalesce(cd_cnpj, cd_cnpj_p)				= cd_cnpj_p) or (coalesce(cd_cnpj::text, '') = ''))
order by
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0),
	coalesce(cd_cnpj, 'xxx');


BEGIN

select	coalesce(ie_valor_consignado,'N')
into STRICT	ie_valor_parametro_w
from	parametro_compras
where	cd_estabelecimento = cd_estabelecimento_p;

select	a.cd_grupo_material,
	a.cd_subgrupo_material,
	a.cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v a
where	a.cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	ie_valor_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_valor_regra_w := ie_valor_regra_w;
	end;
end loop;
close C01;

ie_valor_consignado_w	:= coalesce(ie_valor_regra_w, ie_valor_parametro_w);


return ie_valor_consignado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
