CREATE OR REPLACE FUNCTION obter_regra_via_acesso ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint, cd_convenio_p bigint) RETURNS char AS $body$
DECLARE


ie_retorno_w			char(1);
cd_area_procedimento_w	integer;
cd_especialidade_w		bigint;
cd_grupo_proc_w			bigint;

c01 CURSOR FOR
	SELECT	ie_via_acesso
	from	regra_via_acesso
	where	((coalesce(cd_procedimento::text, '') = '') or (cd_procedimento = cd_procedimento_p))
	and		((coalesce(cd_procedimento::text, '') = '') or (ie_origem_proced = ie_origem_proced_p))
	and		((coalesce(cd_area_procedimento::text, '') = '') or (cd_area_procedimento = cd_area_procedimento_w))
	and		((coalesce(cd_especialidade::text, '') = '') or (cd_especialidade = cd_especialidade_w))
	and		((coalesce(cd_grupo_proc::text, '') = '') or (cd_grupo_proc = cd_grupo_proc_w))
	and 	((coalesce(cd_convenio::text, '') = '') or (cd_convenio = coalesce(cd_convenio_p,cd_convenio)))
	and		cd_estabelecimento	= cd_estabelecimento_p
	and		ie_situacao		= 'A'
	order by coalesce(cd_procedimento, 0),
		 coalesce(cd_grupo_proc, 0),
		 coalesce(cd_especialidade, 0),
		 coalesce(cd_area_procedimento, 0);


BEGIN
select	coalesce(max('S'),'U')
into STRICT	ie_retorno_w
from	regra_via_acesso LIMIT 1;

if (ie_retorno_w = 'S') then
	begin
	ie_retorno_w	:= null;

	select	cd_area_procedimento,
			cd_especialidade,
			cd_grupo_proc
	into STRICT	cd_area_procedimento_w,
			cd_especialidade_w,
			cd_grupo_proc_w
	from	estrutura_procedimento_v
	where	cd_procedimento		= cd_procedimento_p
	and		ie_origem_proced	= ie_origem_proced_p;

	open C01;
	loop
	fetch C01 into ie_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ie_retorno_w	:= ie_retorno_w;
		end;
	end loop;
	close c01;

	exception when others then
		null;
	end;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
