CREATE OR REPLACE FUNCTION obter_reg_mat ( dt_mesano_referencia_p timestamp, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE



ds_retorno_w		bigint;

BEGIN

select   count(*) qt_reg
into STRICT	 ds_retorno_w
from	 lote_producao a,
	 ficha_tecnica b,
	 ficha_tecnica_componente c
where	 a.cd_material 		= b.cd_material
and	 b.nr_ficha_tecnica 	= c.nr_ficha_tecnica
and	 (a.dt_confirmacao IS NOT NULL AND a.dt_confirmacao::text <> '')
and	 a.cd_material = cd_material_p
and	 a.dt_mesano_referencia	= dt_mesano_referencia_p
and	 (c.cd_procedimento IS NOT NULL AND c.cd_procedimento::text <> '')
group by a.cd_material;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
