CREATE OR REPLACE FUNCTION obter_relatorios_diferenciados ( nr_sequencia_p bigint, cd_funcao_p bigint) RETURNS varchar AS $body$
DECLARE

						 
	 
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_setor_atendimento_w 	bigint;
ds_resultado_w		varchar(255);
	

BEGIN 
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	begin 
	select	cd_procedimento, 
		ie_origem_proced, 
		cd_setor_atendimento 
	into STRICT	cd_procedimento_w, 
		ie_origem_proced_w, 
		cd_setor_atendimento_w 
	from	procedimento_paciente 
	where	nr_sequencia  = nr_sequencia_p;
	 
	 
	ds_resultado_w	:= obter_relat_proc(cd_procedimento_w,ie_origem_proced_w,cd_funcao_p,cd_setor_atendimento_w);
	end;
end if;
 
return ds_resultado_w;
 
end;
