CREATE OR REPLACE FUNCTION obter_relat_laudo (nr_seq_laudo_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_relatorio_w		bigint;
ds_resultado_w		varchar(255);

C01 CURSOR FOR 
	SELECT	b.cd_relatorio 
	from	relatorio b, 
		laudo_paciente_relat a 
	where	a.cd_classif_relat		= b.cd_classif_relat 
	and	a.cd_relatorio		= b.cd_relatorio 
	and	a.nr_seq_laudo		= nr_seq_laudo_p;


BEGIN 
 
ds_resultado_w	:= '';
if (nr_seq_laudo_p IS NOT NULL AND nr_seq_laudo_p::text <> '') then 
	open C01;
	loop 
		fetch C01 into cd_relatorio_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
 
		ds_resultado_w	:= ds_resultado_w || cd_relatorio_w || ';';
	end loop;
	close C01;
end if;
 
 
return substr(ds_resultado_w,1, length(ds_resultado_w) - 1);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
