CREATE OR REPLACE FUNCTION obter_relat_proc ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_funcao_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_relatorio_w		bigint;
ds_resultado_w		varchar(255);

C01 CURSOR FOR 
	SELECT	b.cd_relatorio 
	from	relatorio b, 
		procedimento_relatorio a 
	where	a.nr_seq_relatorio	= b.nr_sequencia 
	and	a.cd_procedimento	= cd_procedimento_p 
	and	a.ie_origem_proced	= ie_origem_proced_p 
	and	a.cd_funcao		= cd_funcao_p 
	and	coalesce(a.cd_setor_atendimento, cd_setor_atendimento_p) = cd_setor_atendimento_p;


BEGIN 
 
ds_resultado_w	:= '';
if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then 
	open C01;
	loop 
		fetch C01 into cd_relatorio_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
 
		ds_resultado_w	:= ds_resultado_w || cd_relatorio_w || ';';
	end loop;
	close C01;
end if;
 
 
return substr(ds_resultado_w,1, length(ds_resultado_w) - 1);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
