CREATE OR REPLACE FUNCTION obter_relat_regra_copia (cd_convenio_p bigint, cd_setor_atendimento_p bigint, ie_tipo_atendimento_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_convenio_w			integer;
cd_setor_atendimento_w	integer;
ie_tipo_atendimento_w	smallint;
qt_copia_w			integer;

c01 CURSOR FOR
	SELECT qt_copia
	from relat_regra_copia
	where cd_estabelecimento = cd_estabelecimento_p
	  and coalesce(cd_convenio, cd_convenio_w) = cd_convenio_w
	  and coalesce(cd_setor_atendimento, cd_setor_atendimento_w) = cd_setor_atendimento_w
	  and coalesce(ie_tipo_atendimento,ie_tipo_atendimento_w)	= ie_tipo_atendimento_w
	order by coalesce(ie_tipo_atendimento,0),
		 coalesce(cd_setor_atendimento,0),
		 coalesce(cd_convenio,0);

BEGIN

cd_convenio_w 		:= coalesce(cd_convenio_p,0);
cd_setor_atendimento_w	:= coalesce(cd_setor_atendimento_p,0);
ie_tipo_atendimento_w	:= coalesce(ie_tipo_atendimento_p,0);

open c01;
loop
fetch c01 into qt_copia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

return qt_copia_w;

end;
$body$
