CREATE OR REPLACE FUNCTION obter_rep_calc_pacote (nr_seq_proc_pacote_p bigint, vl_item_p bigint, ie_cancelamento_p text default null, nr_seq_proc_origem_p bigint default null, nr_seq_mat_origem_p bigint default null) RETURNS bigint AS $body$
DECLARE


vl_pacote_w		double precision;
vl_repasse_calc_w	double precision;
vl_retorno_w		double precision := 0;
nr_seq_proc_pacote_w	procedimento_paciente.nr_seq_proc_pacote%type;


BEGIN

if (coalesce(ie_cancelamento_p::text, '') = '') or (ie_cancelamento_p <> 'E') then
	nr_seq_proc_pacote_w	:= nr_seq_proc_pacote_p;
elsif (ie_cancelamento_p = 'E') then
	if (coalesce(nr_seq_proc_origem_p,0) <> 0) then
		begin
		select	nr_seq_proc_pacote
		into STRICT	nr_seq_proc_pacote_w
		from	procedimento_paciente
		where	nr_sequencia = nr_seq_proc_origem_p
		and 	(nr_seq_proc_pacote IS NOT NULL AND nr_seq_proc_pacote::text <> '')
		and		coalesce(cd_motivo_exc_conta::text, '') = '';
		exception
		when others then
			nr_seq_proc_pacote_w	:= null;
		end;	
	elsif (coalesce(nr_seq_mat_origem_p,0) <> 0) then
		begin
		select	nr_seq_proc_pacote
		into STRICT	nr_seq_proc_pacote_w
		from	material_atend_paciente
		where	nr_sequencia = nr_seq_mat_origem_p
		and 	(nr_seq_proc_pacote IS NOT NULL AND nr_seq_proc_pacote::text <> '')
		and		coalesce(cd_motivo_exc_conta::text, '') = '';
		exception
		when others then
			nr_seq_proc_pacote_w	:= null;
		end;
	end if;
end if;

if (nr_seq_proc_pacote_w IS NOT NULL AND nr_seq_proc_pacote_w::text <> '') then

	select 	sum(coalesce(vl_procedimento,0)),
		sum(coalesce(vl_repasse_calc,0))
	into STRICT	vl_pacote_w,
		vl_repasse_calc_w
	from 	procedimento_paciente
	where	nr_sequencia		= nr_seq_proc_pacote_w
	and 	(nr_seq_proc_pacote IS NOT NULL AND nr_seq_proc_pacote::text <> '')
	and	coalesce(cd_motivo_exc_conta::text, '') = '';

	vl_retorno_w	:= dividir_sem_round(vl_item_p, vl_pacote_w) * vl_repasse_calc_w;
else
	vl_retorno_w	:= 0;
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
