CREATE OR REPLACE FUNCTION obter_rep_item_processo_adep (nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_horario_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_prescricao_w	bigint;


BEGIN 
 
if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then 
 
	select	(obter_dados_horario(nr_seq_horario_p,'NP'))::numeric  
	into STRICT	nr_prescricao_w 
	;
 
elsif (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then 
 
	nr_prescricao_w := nr_prescricao_p;
 
end if;
 
return nr_prescricao_w;
 
end;
