SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_req_dispensada_recebida ( nr_requisicao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p = Passando a opção a function retorna se está pendente para o processo informado no campo.
D   - Verifica se está pendente de DISPENSAÇÃO
R   - Verifica se está pendente de RECEBIMENTO

O retono desta function é S ou N, será passado o nrº da requisição e a opção que será verificada.
Está function é utilizada no Atendimento da Requisição (PDA) - Dispensação Requisição
*/
ie_retorno_w		varchar(1) := 'N';

qt_nao_dispensado_w	bigint;
qt_nao_recebido_w		bigint;


BEGIN

if (ie_opcao_p = 'D') then
	begin
	select  	count(*)
	into STRICT	qt_nao_dispensado_w
	from    	item_requisicao_material
	where   	nr_requisicao = nr_requisicao_p
	and     	(dt_atendimento IS NOT NULL AND dt_atendimento::text <> '')
	and     	coalesce(dt_dispensacao::text, '') = '';

	if (qt_nao_dispensado_w > 0) then
		ie_retorno_w := 'S';
	end if;
	end;
end if;

if (ie_opcao_p = 'R') then
	begin
	select	count(*)
	into STRICT	qt_nao_recebido_w
	from    	item_requisicao_material
	where   	nr_requisicao = nr_requisicao_p
	and     	(dt_atendimento IS NOT NULL AND dt_atendimento::text <> '')
	and     	(dt_dispensacao IS NOT NULL AND dt_dispensacao::text <> '')
	and     	coalesce(dt_recebimento::text, '') = '';

	if (qt_nao_recebido_w > 0) then
		ie_retorno_w := 'S';
	end if;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_req_dispensada_recebida ( nr_requisicao_p bigint, ie_opcao_p text) FROM PUBLIC;

