CREATE OR REPLACE FUNCTION obter_responsavel_departamento (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_departamento_w	bigint;	
cd_responsavel_w	varchar(12);				
				 

BEGIN 
 
select	coalesce(max(cd_responsavel),Obter_Pessoa_Fisica_Usuario(wheb_usuario_pck.get_nm_usuario,'C')) 
into STRICT	cd_responsavel_w 
from	departamento_medico 
where	cd_departamento = Obter_Atepacu_paciente(nr_atendimento_p,'MCD') 
and		Obter_se_medico(coalesce(cd_responsavel,Obter_Pessoa_Fisica_Usuario(wheb_usuario_pck.get_nm_usuario,'C')),'M') = 'S';
 
return	cd_responsavel_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
