CREATE OR REPLACE FUNCTION obter_responsavel_funcao ( cd_funcao_p bigint) RETURNS varchar AS $body$
DECLARE

		 
cd_responsavel_w	varchar(10);
		

BEGIN 
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') then 
	begin 
	select	max(obter_pessoa_fisica_usuario(nm_usuario_exec,'C')) 
	into STRICT	cd_responsavel_w 
	from	funcao_grupo_des 
	where	cd_funcao = cd_funcao_p;
	end;
end if;
return cd_responsavel_w;
end;
$body$
LANGUAGE PLPGSQL
