CREATE OR REPLACE FUNCTION obter_responsavel_pf (cd_pessoa_fisica_p text, ie_tipo_complemento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
cd_pessoa_fisica_ref_w	varchar(10);
nm_pessoa_fisica_ref_w	varchar(60);
ds_responsavel_w		varchar(60);


BEGIN 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	select	cd_pessoa_fisica_ref, 
		substr(obter_nome_pf(cd_pessoa_fisica_ref),1,60) 
	into STRICT	cd_pessoa_fisica_ref_w, 
		nm_pessoa_fisica_ref_w 
	from	compl_pessoa_fisica 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	ie_tipo_complemento	= coalesce(ie_tipo_complemento_p,3);
 
	if (coalesce(ie_opcao_p,'C') = 'C') then 
		ds_responsavel_w	:= cd_pessoa_fisica_ref_w;
	elsif (coalesce(ie_opcao_p,'C') = 'N') then 
		ds_responsavel_w	:= nm_pessoa_fisica_ref_w;
	end if;
end if;
 
RETURN	ds_responsavel_w;
 
END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
