SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_resp_agenda_hc ( cd_pessoa_fisica_p text, nr_seq_servico_p bigint, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255) := '';
nm_resp_w			varchar(60);
nr_seq_servico_w		bigint;
ie_exibir_procedimentos_w	varchar(1);

C01 CURSOR FOR 
	SELECT	distinct 
		c.nr_sequencia 
	from	agenda_home_care a, 
		agenda_hc_paciente b, 
		hc_servico c 
	where	b.nr_seq_agenda = a.nr_sequencia 
	and	a.nr_seq_servico = c.nr_sequencia 
	and	b.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	b.dt_agenda = dt_agenda_p 
	and	((c.nr_sequencia = nr_seq_servico_p) 
		or (nr_seq_servico_p = 0) 
		or (ie_exibir_procedimentos_w = 'N')) 
	order by 1;

 
C02 CURSOR FOR 
	SELECT	distinct substr(obter_nome_pf(b.cd_pessoa_fisica),1,60) nm_responsavel 
	from	hc_agenda_prof a, 
		hc_profissional b, 
		agenda_hc_paciente c, 
		agenda_home_care d 
	where	a.nr_seq_prof_hc = b.nr_sequencia 
	and	a.nr_seq_agenda = c.nr_sequencia 
	and	c.nr_seq_agenda = d.nr_sequencia 
	and	c.dt_agenda = dt_agenda_p 
	and	c.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	d.nr_seq_servico = nr_seq_servico_w 
	order by 1;
	

BEGIN 
ie_exibir_procedimentos_w := obter_param_usuario(867, 91, obter_perfil_ativo, Wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_exibir_procedimentos_w);
 
open C01;
loop 
fetch C01 into	 
	nr_seq_servico_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	 
	open C02;
	loop 
	fetch C02 into	 
		nm_resp_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		 
		if (coalesce(ds_retorno_w::text, '') = '') then 
			ds_retorno_w := nm_resp_w;
		else 
			ds_retorno_w := substr(ds_retorno_w || ', ' || nm_resp_w,1,255);
		end if;
		 
		end;
	end loop;
	close C02;
	 
	 
	end;
end loop;
close C01;
 
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
