CREATE OR REPLACE FUNCTION obter_resp_atend_data_opcao (nr_atendimento_p bigint, dt_responsavel_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
nr_seq_resp_w		bigint;
cd_responsavel_w	varchar(10);
nm_responsavel_w	varchar(60);
vl_retorno_w		varchar(60);


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (dt_responsavel_p IS NOT NULL AND dt_responsavel_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then 
	/* obter registro responsavel */
 
	select	coalesce(min(nr_sequencia),0) 
	into STRICT	nr_seq_resp_w 
	from	atend_enfermagem_resp 
	where	nr_atendimento = nr_atendimento_p 
	and	((dt_responsavel_p between dt_inicio_resp and dt_final_resp) 
	or (dt_responsavel_p > dt_inicio_resp and coalesce(dt_final_resp::text, '') = ''));
 
	/* validar registro responsavel */
 
	if (coalesce(nr_seq_resp_w,0) > 0) then 
		select	cd_pessoa_fisica, 
			substr(obter_nome_pf(cd_pessoa_fisica),1,60) 
		into STRICT	cd_responsavel_w, 
			nm_responsavel_w 
		from	atend_enfermagem_resp 
		where	nr_sequencia = nr_seq_resp_w;
 
		/* validar retorno */
 
		if (ie_opcao_p = 'C') then 
			vl_retorno_w := cd_responsavel_w;
		elsif (ie_opcao_p = 'N') then 
			vl_retorno_w := nm_responsavel_w;			
		end if;
	end if;
end if;
 
return vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
