SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_resp_atend_data (nr_atendimento_p bigint, dt_responsavel_p timestamp) RETURNS varchar AS $body$
DECLARE

 
nm_responsavel_w	varchar(60);
nr_sequencia_w	bigint;


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (dt_responsavel_p IS NOT NULL AND dt_responsavel_p::text <> '') then 
	select	coalesce(min(nr_sequencia),0) 
	into STRICT	nr_sequencia_w 
	from	atend_enfermagem_resp 
	where	nr_atendimento = nr_atendimento_p 
	and	((dt_responsavel_p between dt_inicio_resp and dt_final_resp) 
	or (dt_responsavel_p > dt_inicio_resp and coalesce(dt_final_resp::text, '') = ''));
 
	if (coalesce(nr_sequencia_w,0) > 0) then 
		select	substr(obter_nome_pf(cd_pessoa_fisica),1,60) 
		into STRICT	nm_responsavel_w 
		from	atend_enfermagem_resp 
		where	nr_sequencia = nr_sequencia_w;
	end if;
end if;
 
return nm_responsavel_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
