CREATE OR REPLACE FUNCTION obter_resp_atend_paciente (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_responsavel_w varchar(100);

BEGIN
select	substr(obter_nome_pessoa_fisica(b.cd_pessoa_responsavel, null),1,40) 
into STRICT	ds_Responsavel_w 
from	pessoa_fisica a, 
	atendimento_paciente b 
where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
and 	b.nr_atendimento = nr_atendimento_p;
return ds_responsavel_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
